EcoRI = ""
EcoRI = EcoRI.upper()


def comp(EcoRI):  # 상보적 염기서열 -mRNA화
    comp_dict = {'A': 'T', 'T': 'A', 'C': 'G', 'G': 'C'}
    EcoRI_comp = ""
    for char in EcoRI:
        if char in comp_dict:
            EcoRI_comp += comp_dict[char]
    return EcoRI_comp


def rev(EcoRI):  # 역순 염기서열 -mRNA화
    comp_dict = {'A': 'A', 'T': 'T', 'C': 'C', 'G': 'G'}
    rev_str = "".join(reversed(EcoRI))
    EcoRI_rev = ""
    for char in rev_str:
        if char in comp_dict:
            EcoRI_rev += comp_dict[char]
    return EcoRI_rev


def cke(EcoRI, pattern):  # 제한효소자리 검사
    pattern_list = []
    for i in range(0, len(EcoRI) - len(pattern)):
        if EcoRI[i:i + len(pattern)] == pattern:
            pattern_list.append((i + 1, EcoRI[i:i + len(pattern)]))
    if len(pattern_list) == 0:
        return None
    else:
        return pattern_list


pattern = comp(input('사용할 제한효소의 염기서열을입력해주세요.').upper())
ck = cke(EcoRI, pattern)
print(ck)


if ck != None:
    import matplotlib.pyplot as plt

    y = []
    color = []
    label = []
    k = 0

    for j in range(len(ck)):
        y.append(int(ck[j][0])-k-1)
        color.append("w")
        y.append(len(pattern))
        color.append("r")
        k = ck[j][0]+len(pattern)-1

    y.append(len(EcoRI)-k)
    color.append("w")


    #  PGLO Map 표시. 제한효소가 붙는 부분과 각각의 사이 간격이 보여진다. 12방향이 시작지점, 시계방향으로 bp 번호가 증가한다.
    plt.figure()
    plt.pie(y, colors=color, labels=y, startangle=90, counterclock=False)
    plt.axis('equal')
    centre_circle = plt.Circle((0,0), 0.90, color='white')
    plt.gca().add_artist(centre_circle)
    plt.show()
